// Comprehensive Customer Health Score Calculator
// Combines data from HubSpot, Zendesk, and Notion

const items = $input.all();
const results = [];

for (const item of items) {
  const client = item.json;

  // Extract client data from Notion
  const clientName = client.properties['Client Name']?.title?.[0]?.plain_text || 'Unknown Client';
  const clientId = client.id;

  if (!clientName || clientName === 'Unknown Client') {
    continue;
  }

  // Get API data from previous nodes
  const hubspotCompany = $node['Get HubSpot Company Data'].json?.results?.[0] || {};
  const hubspotMeetings = $node['Get HubSpot Meetings (3mo)'].json?.results || [];
  const zendeskTickets = $node['Get Zendesk Support Tickets'].json?.results || [];
  const zendeskCritical = $node['Get Critical Zendesk Tickets'].json?.results || [];

  // Feature Adoption Data (from Notion - manually entered)
  const featuresUsed = client.properties['Features Used']?.number || 0;
  const totalFeatures = client.properties['Total Features Available']?.number || 20;
  const advancedFeaturesUsed = client.properties['Advanced Features Used']?.number || 0;

  // Calculate Feature Adoption Score
  const featureAdoptionRate = totalFeatures > 0 ? (featuresUsed / totalFeatures) * 100 : 0;
  const featureScore = Math.min(100, 
    (featureAdoptionRate * 0.7) + (advancedFeaturesUsed * 5)
  );

  // Relationship Data - Combined HubSpot + Notion
  const totalMeetings = hubspotMeetings.length;

  // Analyze meeting sentiment from HubSpot data
  let positiveMeetings = 0;
  let meetingTopics = [];
  let meetingHighlights = [];

  hubspotMeetings.forEach(meeting => {
    const title = meeting.properties?.hs_meeting_title || '';
    const outcome = meeting.properties?.hs_meeting_outcome || '';
    const notes = meeting.properties?.hs_meeting_notes || meeting.properties?.hs_meeting_body || '';

    // Count as positive if outcome contains positive keywords
    if (outcome.toLowerCase().includes('positive') || 
        outcome.toLowerCase().includes('success') ||
        outcome.toLowerCase().includes('good') ||
        notes.toLowerCase().includes('satisfied') ||
        notes.toLowerCase().includes('pleased')) {
      positiveMeetings++;
    }

    if (title) meetingTopics.push(title);
    if (notes) meetingHighlights.push(notes.substring(0, 100) + '...');
  });

  // Renewal and contact timing
  const renewalDateStr = client.properties['Renewal Date']?.date?.start;
  const renewalDate = renewalDateStr ? new Date(renewalDateStr) : new Date(Date.now() + 365*24*60*60*1000);
  const daysToRenewal = Math.max(0, 
    Math.ceil((renewalDate - new Date()) / (1000 * 60 * 60 * 24))
  );

  // Last contact from HubSpot or Notion
  const hubspotLastContact = hubspotCompany.properties?.notes_last_updated;
  const notionLastContact = client.properties['Last Contact Date']?.date?.start;

  let lastContactDays = 999;
  if (hubspotLastContact) {
    lastContactDays = Math.min(lastContactDays, 
      Math.ceil((new Date() - new Date(hubspotLastContact)) / (1000 * 60 * 60 * 24))
    );
  }
  if (notionLastContact) {
    lastContactDays = Math.min(lastContactDays, 
      Math.ceil((new Date() - new Date(notionLastContact)) / (1000 * 60 * 60 * 24))
    );
  }

  // Communication assessment (from Notion)
  const communicationRating = client.properties['Communication Rating']?.select?.name || 'Average';
  const relationshipStrength = client.properties['Relationship Strength']?.select?.name || 'Neutral';

  // Calculate Relationship Score with enhanced HubSpot data
  let relationshipScore = 100;

  // Meeting frequency scoring (enhanced with HubSpot data)
  if (totalMeetings < 3) relationshipScore -= 25;
  else if (totalMeetings < 6) relationshipScore -= 15;
  else if (totalMeetings >= 10) relationshipScore += 5;

  // Meeting sentiment scoring (from HubSpot analysis)
  if (totalMeetings > 0) {
    const positiveRatio = positiveMeetings / totalMeetings;
    if (positiveRatio < 0.3) relationshipScore -= 40;
    else if (positiveRatio < 0.5) relationshipScore -= 25;
    else if (positiveRatio < 0.7) relationshipScore -= 10;
    else if (positiveRatio >= 0.8) relationshipScore += 10;
  }

  // Renewal risk scoring
  if (daysToRenewal < 30) relationshipScore -= 30;
  else if (daysToRenewal < 60) relationshipScore -= 20;
  else if (daysToRenewal < 90) relationshipScore -= 10;

  // Communication responsiveness (enhanced with HubSpot data)
  if (lastContactDays > 21) relationshipScore -= 25;
  else if (lastContactDays > 14) relationshipScore -= 15;
  else if (lastContactDays > 7) relationshipScore -= 5;
  else if (lastContactDays <= 3) relationshipScore += 5;

  // HubSpot engagement indicators
  const hubspotLastVisit = hubspotCompany.properties?.hs_analytics_last_visit_timestamp;
  if (hubspotLastVisit) {
    const daysSinceVisit = Math.ceil((new Date() - new Date(parseInt(hubspotLastVisit))) / (1000 * 60 * 60 * 24));
    if (daysSinceVisit > 30) relationshipScore -= 10;
    else if (daysSinceVisit <= 7) relationshipScore += 5;
  }

  // Communication quality and relationship strength (from Notion)
  switch (communicationRating) {
    case 'Excellent': relationshipScore += 15; break;
    case 'Good': relationshipScore += 5; break;
    case 'Below Average': relationshipScore -= 15; break;
    case 'Poor': relationshipScore -= 25; break;
  }

  switch (relationshipStrength) {
    case 'Champion': relationshipScore += 20; break;
    case 'Strong': relationshipScore += 10; break;
    case 'Weak': relationshipScore -= 15; break;
    case 'Strained': relationshipScore -= 30; break;
  }

  relationshipScore = Math.max(0, Math.min(100, relationshipScore));

  // Determine relationship color
  let relationshipColor = 'Red';
  if (relationshipScore >= 75) relationshipColor = 'Green';
  else if (relationshipScore >= 50) relationshipColor = 'Yellow';

  // Engagement Data (from Notion)
  const onboardingProgress = client.properties['Onboarding Progress %']?.number || 0;
  const learningModulesCompleted = client.properties['Learning Modules Completed']?.number || 0;
  const certificationsEarned = client.properties['Certifications Earned']?.number || 0;
  const trainingAttendanceRate = client.properties['Training Attendance Rate']?.number || 0;

  const engagementScore = Math.min(100,
    (onboardingProgress * 0.4) + 
    (learningModulesCompleted * 4) + 
    (certificationsEarned * 12) +
    (trainingAttendanceRate * 0.4)
  );

  // ARR Data (Notion + HubSpot)
  const currentARR = client.properties['Current ARR']?.number || 
    hubspotCompany.properties?.hs_total_deal_value || 0;
  const arrTrend = client.properties['ARR Trend']?.select?.name || 'Stable';
  const paymentStatus = client.properties['Payment Status']?.select?.name || 'Current';
  const contractType = client.properties['Contract Type']?.select?.name || 'Standard';

  // Calculate ARR Health Score
  let arrScore = 100;

  switch (paymentStatus) {
    case 'Late': arrScore -= 25; break;
    case 'Overdue': arrScore -= 45; break;
    case 'Failed': arrScore -= 65; break;
  }

  switch (arrTrend) {
    case 'Increasing': arrScore += 15; break;
    case 'Decreasing': arrScore -= 25; break;
  }

  if (currentARR < 1000) arrScore -= 15;
  else if (currentARR > 10000) arrScore += 10;

  if (contractType === 'At Risk') arrScore -= 30;
  else if (contractType === 'Enterprise') arrScore += 5;

  arrScore = Math.max(0, Math.min(100, arrScore));

  // Support Data from Zendesk
  const supportTickets30d = zendeskTickets.length;
  const criticalTickets = zendeskCritical.length;

  // Calculate average satisfaction from Zendesk tickets
  let totalSatisfaction = 0;
  let satisfactionCount = 0;
  zendeskTickets.forEach(ticket => {
    if (ticket.satisfaction_rating && ticket.satisfaction_rating.score) {
      totalSatisfaction += ticket.satisfaction_rating.score === 'good' ? 5 : 
                          ticket.satisfaction_rating.score === 'bad' ? 1 : 3;
      satisfactionCount++;
    }
  });
  const avgSatisfaction = satisfactionCount > 0 ? totalSatisfaction / satisfactionCount : 3;

  // Count escalated tickets
  const escalatedTickets = zendeskTickets.filter(ticket => 
    ticket.status === 'pending' && ticket.priority === 'high'
  ).length;

  // Calculate Support Health Score
  const supportScore = Math.max(0, Math.min(100,
    100 - (supportTickets30d * 2) - (criticalTickets * 12) - (escalatedTickets * 8) + 
    ((avgSatisfaction - 3) * 15)
  ));

  // Calculate Overall Health Score
  const overallHealthScore = Math.round(
    (featureScore * 0.25) +
    (relationshipScore * 0.25) +
    (engagementScore * 0.20) +
    (arrScore * 0.15) +
    (supportScore * 0.15)
  );

  // Determine health status
  let healthStatus = 'Critical';
  if (overallHealthScore >= 80) healthStatus = 'Healthy';
  else if (overallHealthScore >= 60) healthStatus = 'At Risk';

  // Generate insights with API data
  const keyInsights = [];
  if (featureScore < 60) keyInsights.push('Low feature adoption');
  if (relationshipColor === 'Red') keyInsights.push('Relationship at risk');
  if (engagementScore < 50) keyInsights.push('Poor onboarding progress');
  if (arrScore < 70) keyInsights.push('Payment or billing issues');
  if (supportScore < 60) keyInsights.push('High support burden');
  if (daysToRenewal < 60 && overallHealthScore < 70) keyInsights.push('Renewal risk');
  if (lastContactDays > 21) keyInsights.push('Overdue for contact');
  if (criticalTickets > 2) keyInsights.push('Multiple critical support issues');
  if (totalMeetings === 0) keyInsights.push('No recent meetings scheduled');

  // Expansion opportunities with API insights
  const expansionOpportunities = [];
  if (featureScore > 80 && currentARR < 5000) expansionOpportunities.push('High engagement - upsell ready');
  if (relationshipScore > 85) expansionOpportunities.push('Strong relationship - expansion ready');
  if (engagementScore > 90) expansionOpportunities.push('Highly engaged - advocate potential');
  if (arrTrend === 'Increasing') expansionOpportunities.push('Growing usage - expansion timing');
  if (certificationsEarned > 2) expansionOpportunities.push('Power user - advanced features');
  if (supportScore > 85 && totalMeetings > 6) expansionOpportunities.push('Satisfied customer - referral potential');

  // Action items with API context
  const actionItems = [];
  if (relationshipColor === 'Red') actionItems.push('Schedule immediate check-in call');
  if (daysToRenewal < 60 && overallHealthScore < 70) actionItems.push('Initiate renewal conversation');
  if (supportScore < 50) actionItems.push('Review support ticket trends');
  if (onboardingProgress < 50) actionItems.push('Re-engage with onboarding team');
  if (lastContactDays > 21) actionItems.push('Reach out - overdue for contact');
  if (featureScore < 40) actionItems.push('Schedule product training session');
  if (paymentStatus !== 'Current') actionItems.push('Address payment issues');
  if (criticalTickets > 1) actionItems.push('Escalate critical support issues');
  if (totalMeetings === 0 && daysToRenewal < 180) actionItems.push('Schedule relationship building meeting');

  // Action flags
  const needsImmediateAttention = (
    overallHealthScore < 50 || 
    relationshipColor === 'Red' || 
    actionItems.length > 2 ||
    paymentStatus === 'Overdue' ||
    criticalTickets > 2 ||
    (daysToRenewal < 30 && overallHealthScore < 70)
  );

  const expansionReady = (
    overallHealthScore > 80 && 
    expansionOpportunities.length > 0 &&
    relationshipScore > 70 &&
    supportScore > 70
  );

  const renewalRisk = (
    daysToRenewal < 90 && 
    (overallHealthScore < 70 || relationshipColor === 'Red' || 
     paymentStatus !== 'Current' || supportScore < 60)
  );

  results.push({
    clientId: clientId,
    clientName: clientName,

    // Calculated scores
    overallHealthScore: overallHealthScore,
    healthStatus: healthStatus,
    featureAdoptionScore: Math.round(featureScore),
    relationshipScore: Math.round(relationshipScore),
    relationshipColor: relationshipColor,
    engagementScore: Math.round(engagementScore),
    arrHealthScore: Math.round(arrScore),
    supportHealthScore: Math.round(supportScore),

    // API-enhanced data
    totalMeetings: totalMeetings,
    positiveMeetings: positiveMeetings,
    meetingTopics: meetingTopics.slice(0, 3).join('; '),
    meetingHighlights: meetingHighlights.slice(0, 2).join('; '),
    supportTickets30d: supportTickets30d,
    criticalTickets: criticalTickets,
    supportSatisfaction: Math.round(avgSatisfaction * 100) / 100,

    // Insights and actions
    keyInsights: keyInsights,
    expansionOpportunities: expansionOpportunities,
    actionItems: actionItems,

    // Flags
    needsImmediateAttention: needsImmediateAttention,
    expansionReady: expansionReady,
    renewalRisk: renewalRisk,

    // Metadata
    calculatedAt: new Date().toISOString(),
    daysToRenewal: daysToRenewal,
    lastContactDays: lastContactDays,
    dataSource: 'HubSpot + Zendesk + Notion'
  });
}

return results.map(result => ({ json: result }));
